import { authService, dbService } from "fbase";
import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

export default ({ userObj }) => {
  const history = useHistory();
  const [historyNtweet, sethistoryNtweet] = useState();
  const [NewDisplay, setNewDisplay] = useState(userObj.displayName);
  const onLogOutClick = () => {
    authService.signOut();
    history.push("/");
  };
  const onChange = (event) => {
    const {
      target: { value },
    } = event;
    setNewDisplay(value);
  };
  const onSubmit = async (event) => {
    event.preventDefault();
    if (userObj.displayName !== NewDisplay) {
      await userObj.updateProfile({
        displayName: NewDisplay,
      });
    }
  };

  const getMyntweet = async () => {
    const getntweet = await dbService.collection("ntweets").where("creatorId", "==", userObj.uid).orderBy("createdAt", "desc").get();
    historyarray = getntweet.docs.map((doc) => doc.data());
    sethistoryNtweet(historyarray);
  };
  useEffect(() => {
    getMyntweet();
  }, []);
  return (
    <>
      <form>
        <input type="text" placeholder="Display Name" />
        <input type="submit" value="Update Profile" />
      </form>
      <button onClick={onLogOutClick}>Log Out</button>
      <h3>{historyarray[0].text}</h3>
    </>
  );
};
