import { dbService } from "fbase";
import React, { useEffect, useState } from "react";

const Home = () => {
  const [ntweet, setNtweet] = useState("");
  const [ntweets, setNtweets] = useState([]);
  const getNtweets = async () => {
    const dbntweets = await dbService.collection("ntweets").get();
    dbntweets.forEach((document) => {
      const ntweetObject = {
        ...document.data(),
        id: document.id,
      };
      setNtweets((prev) => [ntweetObject, ...prev]);
    }); // 새로 작성한 것들과 이전의 것들을 합쳐서 배열로 리턴함.
  };
  useEffect(() => {
    getNtweets();
  }, []);
  //   useEffect(() => {
  //     dbService.collection("ntweets").get();
  //   }, []);
  const onSubmit = async (event) => {
    event.preventDefault();
    await dbService.collection("ntweets").add({
      text: ntweet,
      createdAt: Date.now(),
    });
    setNtweet("");
  };
  const onChange = (event) => {
    const {
      target: { value },
    } = event;
    setNtweet(value);
  };
  console.log(ntweets);
  return (
    <div>
      <form onSubmit={onSubmit}>
        <input value={ntweet} onChange={onChange} type="text" placeholder="What's on your mind?" maxLength={120} />
        <input type="submit" value="Ntweet" />
      </form>
      <div>
        {ntweets.map((ntweet) => (
          <div key={ntweet.id}>
            <h4>{ntweet.ntweet}</h4>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
