import { dbService, storageService } from "fbase";
import { v4 as uuidv4 } from "uuid";
import React, { useEffect, useState } from "react";
import Ntweet from "components/Ntweet";

const Home = ({ userObj }) => {
  const [ntweet, setNtweet] = useState("");
  const [ntweets, setNtweets] = useState([]);
  const [attachment, setAttachment] = useState();
  // 이 방법을 하면 리렌더링되서 실시간 반영이 안됨.
  //   const getNtweets = async () => {
  //     const dbntweets = await dbService.collection("ntweets").get();
  //     dbntweets.forEach((document) => {
  //       const ntweetObject = {
  //         ...document.data(),
  //         id: document.id,
  //       };
  //       setNtweets((prev) => [ntweetObject, ...prev]);
  //     }); // 새로 작성한 것들과 이전의 것들을 합쳐서 배열로 리턴함.
  //   };
  useEffect(() => {
    // getNtweets();
    dbService.collection("ntweets").onSnapshot((snapshot) => {
      const ntweetArray = snapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setNtweets(ntweetArray);
    });
  }, []);
  //   useEffect(() => {
  //     dbService.collection("ntweets").get();
  //   }, []);
  const onSubmit = async (event) => {
    event.preventDefault();
    const fileRef = storageService.ref().child(`${userObj.uid}/${uuidv4()}`);
    const response = await fileRef.putString(attachment, "data_url");
    // await dbService.collection("ntweets").add({
    //   text: ntweet,
    //   createdAt: Date.now(),
    //   creatorId: userObj.uid,
    // });
    // setNtweet("");
  };
  const onChange = (event) => {
    const {
      target: { value },
    } = event;
    setNtweet(value);
  };
  const onFileChange = (event) => {
    const {
      target: { files },
    } = event;
    const theFile = files[0]; //log에서 알아냄 후에 fileReader API 사용
    const reader = new FileReader();
    reader.onloadend = (finishedEvent) => {
      const {
        currentTarget: { result },
      } = finishedEvent;
      setAttachment(result); //url
    };
    reader.readAsDataURL(theFile);
  };
  const onClearPhotoClick = () => setAttachment(null);
  return (
    <div>
      <form onSubmit={onSubmit}>
        <input value={ntweet} onChange={onChange} type="text" placeholder="What's on your mind?" maxLength={120} />
        <input type="file" accept="image/*" onChange={onFileChange} />
        <input type="submit" value="Ntweet" />
        {attachment && (
          <div>
            <img src={attachment} width="50px" height="50px" />
            <button onClick={onClearPhotoClick}>Clear Photo</button>
          </div>
        )}
      </form>
      <div>
        {ntweets.map((ntweet) => (
          <Ntweet key={ntweet.id} ntweetObj={ntweet} isOwner={ntweet.creatorId === userObj.uid} />
        ))}
      </div>
    </div>
  );
};

export default Home;
